var http_request=new Array();var whatFunction=new Array();function makeRequest(b,a,c){nextReq=http_request.length;http_request[nextReq]=false;whatFunction[nextReq]=c;if(window.XMLHttpRequest){http_request[nextReq]=new XMLHttpRequest();}else{if(window.ActiveXObject){try{http_request[nextReq]=new ActiveXObject("Msxml2.XMLHTTP");}catch(d){try{http_request[nextReq]=new ActiveXObject("Microsoft.XMLHTTP");}catch(d){}}}}if(!http_request[nextReq]){alert("Giving up :( Cannot create an XMLHTTP instance");}try{http_request[nextReq].onreadystatechange=checkStatus;http_request[nextReq].open("GET",b+"?"+a,true);http_request[nextReq].send(null);}catch(d){}}function makePOSTRequest(a,b,c){nextReq=http_request.length;http_request[nextReq]=false;whatFunction[nextReq]=c;if(window.XMLHttpRequest){http_request[nextReq]=new XMLHttpRequest();if(http_request[nextReq].overrideMimeType){http_request[nextReq].overrideMimeType("text/html");}}else{if(window.ActiveXObject){try{http_request[nextReq]=new ActiveXObject("Msxml2.XMLHTTP");}catch(d){try{http_request[nextReq]=new ActiveXObject("Microsoft.XMLHTTP");}catch(d){}}}}if(!http_request[nextReq]){alert("Cannot create XMLHTTP instance");return false;}http_request[nextReq].onreadystatechange=checkStatus;http_request[nextReq].open("POST",a,true);http_request[nextReq].setRequestHeader("Content-type","application/x-www-form-urlencoded");http_request[nextReq].send(b);}function checkStatus(){for(var i=0;i<http_request.length;i++){if(http_request[i]!=null){if(http_request[i].readyState==4){if(http_request[i].status==200){if(whatFunction[i]!=null){var tempHolder=whatFunction[i];whatFunction[i]=null;try{eval(tempHolder);}catch(e){}http_request[i]=null;}}else{}}}}}function writeInnerHTML(a,b){myDiv=document.getElementById(b);myDiv.innerHTML=a;}